"use strict";document.addEventListener("DOMContentLoaded",()=>{const c=document.querySelectorAll(".categories__block_title");c.forEach((t,e)=>{t.addEventListener("click",()=>{c.forEach(e=>{e.classList.contains("categories__block_title-active")&&e!==t&&e.classList.remove("categories__block_title-active")}),t.classList.toggle("categories__block_title-active")})})});