"use strict";document.addEventListener("DOMContentLoaded",()=>{const t=document.querySelectorAll(".categories__block_title"),e=document.querySelectorAll(".accordion__block_title");t.forEach(c=>{c.addEventListener("click",()=>{t.forEach(t=>{t.classList.contains("categories__block_title-active")&&t!==c&&t.classList.remove("categories__block_title-active")}),c.classList.toggle("categories__block_title-active")})}),e.forEach(c=>{c.addEventListener("click",()=>{e.forEach(t=>{t.classList.contains("accordion__block_title-active")&&t!==c&&t.classList.remove("accordion__block_title-active")}),c.classList.toggle("accordion__block_title-active")})})});