"use strict";document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelectorAll(".categories__block_title");e.forEach(t=>{t.addEventListener("click",()=>{e.forEach(e=>{e.classList.contains("categories__block_title-active")&&e!==t&&e.classList.remove("categories__block_title-active")}),t.classList.toggle("categories__block_title-active")})})});